'use strict';

module.exports = function (grunt) {

    var eb = require('espresso/core/commands/build.js');
    var path = require('path');

    grunt.registerTask('espresso', 'Call espresso build', function () {
      
        var done = this.async();
        var args = {
            "directory": path.resolve('.'),
            "help": false,
            "onFinish": function () {
                done();
            }
        };
        eb.run(args);
    });
};